#EXO7
def epenser_handle(array_twittos)
  puts "__________Question n°7__________"
  puts " "
  counter = 0
  array_twittos.each do |index|
    if(index.include? "@epenser")
      then puts "Epenser est à la #{counter}eme place"
          puts "_________________________________"
          puts " "
          puts " "
          puts " "
      else counter = (counter + 1)
    end
  end
end



#ORCHESTRE
def perform
  twittos = array_twittos
  twittoslength = remove_arobase(twittos)
  cmb_handle(array_twittos)
  little_handle(twittoslength)
  fiv_handle(twittoslength)
  up_handle(twittoslength)
  alpha_handle(twittoslength)
  size_handle(twittoslength)
  epenser_handle(array_twittos)
end

